package OOPS;

class sub1 {
	void m1() {
		System.out.println("Super class");
	}
}
public class inheritance extends sub1 {
	void m2() {
	System.out.println("Sub-Class");
}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		inheritance a=new inheritance();
		a.m1();
		a.m2();
	}

}



